traderjoe_router = load_contract(w3=w3, address=w3.toChecksumAddress('0x60aE616a2155Ee3d9A68541Ba4544862310933d4'), abi=traderjoe_abi)


def swap_ptp_to_destcoin(addr, pkey, dest_coin):
	#address to swap to
    nonce = w3.eth.get_transaction_count(addr)
    avail_ptp = get_ptp_balance(addr)
    txn = traderjoe_router.functions.swapExactTokensForTokens(
      avail_ptp,
      0, #max slippage
      [w3.toChecksumAddress('0x22d4002028f537599bE9f666d1c4Fa138522f9c8'), w3.toChecksumAddress('0xB31f66AA3C1e785363F0875A1B74E27b85FD66c7'), w3.toChecksumAddress(dest_coin)], #ptp to avax to dest_coin
      addr,
      (int(time.time()) + 1000000)
    ).buildTransaction({
      'from': addr,
      'gas': 219985,
      'gasPrice': w3.toWei(34,'gwei'),
      'nonce': nonce,
    })

    signed_txn = w3.eth.account.sign_transaction(txn, private_key=pkey)
    tx_token = w3.eth.send_raw_transaction(signed_txn.rawTransaction)


def swap_ptp_to_usdce(addr, pkey):
    logging.info(f'{time.ctime(time.time())}: Swapping $PTP to $USDC.e')
    swap_ptp_to_destcoin(addr, pkey, dest_coin='0xA7D7079b0FEaD91F3e65f86E8915Cb59c1a4C664')

def swap_ptp_to_usdte(addr, pkey):
    logging.info(f'{time.ctime(time.time())}: Swapping $PTP to $USDT.e')
    swap_ptp_to_destcoin(addr, pkey, dest_coin='0xc7198437980c041c805A1EDcbA50c1Ce5db95118')

def swap_ptp_to_daie(addr, pkey):
    logging.info(f'{time.ctime(time.time())}: Swapping $PTP to $DAI.e')
    swap_ptp_to_destcoin(addr, pkey, dest_coin='0xd586E7F844cEa2F87f50152665BCbc2C279D8d70')


def swap_ptp(info):
    time.sleep(info['intervals'])
    if info['to'] == 'usdce':
        swap_ptp_to_usdce(info['sender_address'], info['private'])
    elif info['to'] == 'usdte':
        swap_ptp_to_usdte(info['sender_address'], info['private'])
    elif info['to'] == 'daie':
        swap_ptp_to_daie(info['sender_address'], info['private'])
    else:
        assert("please set TO in config to be either 'usdte', 'usdce', or 'daie'")
    time.sleep(info['intervals'])
